# CIステップスクリプト用の共通ログ関数

step() {
  echo ""
  echo "──────────────────────────────────────────────────────────────────────"
  echo "▶️  $1"
  echo "──────────────────────────────────────────────────────────────────────"
  return 0 # 成功を示す
}

success() {
  echo "✅ $1"
  return 0 # 成功を示す
}

fail() {
  echo "❌ エラー: $1" >&2 # エラーメッセージを標準エラー出力へ
  return 1 # 失敗を示す終了コードを返す
}

export -f step
export -f success
export -f fail 